// import "./App.css";
import React, { useEffect, useState } from "react";
import CreateForm from "./components/CreateForm";
import "./App.css";
import "bootstrap/dist/css/bootstrap.min.css";
import CardForm from "./components/CardForm";

function App() {
    const [marked, setMarked] = useState(false);
    const [input, setInput] = useState({ id: "", name: "", text: "", marked: false });
    const [contactList, setContactList] = useState([
        { id: "0", name: "Ali", text: "9 888 777 66 55" },
    ]);

    function handleChange(e) {
        const { name, value } = e;
        setInput((prevValue) => {
            return { ...prevValue, [name]: value, id: Date.now() };
        });
    }
    function createContact(e) {
        e.preventDefault();
        setContactList([...contactList, input]);
        setInput({ name: "", text: "" });
    }

    // function markContact(idNum) {
    //     setContactList(contactList.map((contact) => idNum !== contact.id));
    // }

    function deleteContact(idNum) {
        setContactList(contactList.filter((contact) => idNum !== contact.id));
    }

    return (
        <div className="App">
            <CreateForm
                name={input.name}
                text={input.text}
                onChange={handleChange}
                onClick={(e) => createContact(e)}
            />

            {contactList.map((contact, index) => (
                <CardForm
                    key={index}
                    title={contact.name}
                    text={contact.text}
                    id={contact.id}
                    marked={contact.marked}
                    mark={markContact}
                    delete={deleteContact}
                />
            ))}
        </div>
    );
}

export default App;

