// import "./App.css";
import React, { useEffect, useState } from "react";
import CreateForm from "./components/CreateForm";
import "./App.css";
import "bootstrap/dist/css/bootstrap.min.css";
import CardForm from "./components/CardForm";

function App() {
    const [input, setInput] = useState({ id: "", name: "", text: "", marked: false });
    const [contactList, setContactList] = useState([]);

    function handleChange(e) {
        const { name, value } = e;
        setInput((prevValue) => {
            return { ...prevValue, [name]: value, id: Date.now() };
        });
    }
    async function createContact(e) {
        e.preventDefault();
        setInput({ name: "", text: "" });

        await request("/api", "POST");
        // setContactList([...contactList, input]);
    }

    function deleteContact(idNum) {
        setContactList(contactList.filter((contact) => idNum !== contact.id));
    }

    // async function request(url, method = "GET", data = null) {
    //     try {
    //         const headers = {};
    //         let body;

    //         if (data) {
    //             headers["Content-Type"] = "application/json";
    //             body = JSON.stringify(data);
    //         }

    //         const response = await fetch(url, {
    //             method,
    //             headers,
    //             body,
    //         });
    //         return await response.json();
    //     } catch (e) {
    //         console.log(e.message);
    //     }
    // }

    useEffect(() => {
        const requestOptions = {
            method: "POST",
            headers: { "Content-Type": "application/json" },
            body: JSON.stringify({ title: "React Hooks POST Request" }),
        };
        fetch("/api", requestOptions)
            .then((response) => response.json())
            .then((data) => setContactList(data.id));

        // const list = await request("/api");
        // setContactList(list);
        // fetch("/api")
        //     .then((res) => res.json())
        //     .then((data) => setContactList(data));
    }, []);

    return (
        <div className="App">
            <CreateForm
                name={input.name}
                text={input.text}
                onChange={handleChange}
                onClick={(e) => createContact(e)}
            />

            {contactList.map((contact, index) => (
                <CardForm
                    key={index}
                    title={contact.name}
                    text={contact.text}
                    id={contact.id}
                    marked={contact.marked}
                    delete={deleteContact}
                />
            ))}
        </div>
    );
}

export default App;

