import CardForm from "./components/Card";

function App() {
    return (
        <div className="App">
            <CreateForm />
            {typeof dataBackend.users === "undefined" ? (
                <p> Loading...</p>
            ) : (
                dataBackend.users.map((person, index) => (
                    <CardForm key={index} title={person.name} text={person.age} />
                ))
            )}
        </div>
    );
}

